import React, { useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import './style.css';

function AddDestination() {
  const [form, setForm] = useState({ name: '', location: '', description: '' });
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const [success, setSuccess] = useState(false);
  const navigate = useNavigate();

  const handleChange = (e) => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setError(null);
    setSuccess(false);

    try {
      await axios.post('http://localhost:5000/api/destinations', form);
      setSuccess(true);
      setForm({ name: '', location: '', description: '' });
      setTimeout(() => {
        navigate("/destinations");
      }, 1500);
    } catch (err) {
      setError('Failed to add destination. Please try again.');
    } finally {
      setLoading(false);
    }
  };

  return (
    <div>
      <form onSubmit={handleSubmit}>
        <input name="name" placeholder="Name" onChange={handleChange} value={form.name} /><br />
        <input name="location" placeholder="Location" onChange={handleChange} value={form.location} /><br />
        <input name="description" placeholder="Description" onChange={handleChange} value={form.description} /><br />
        <button type="submit" disabled={loading}>{loading ? 'Adding...' : 'Add Destination'}</button>
      </form>

      {success && <p>Destination added successfully!</p>}
      {error && <p style={{ color: 'red' }}>{error}</p>}
    </div>
  );
}

export default AddDestination;
